import './App.css';

class App extends React.Component {
  componentDidMount() {
    const url = 'https://dog.ceo/api/breeds/image/random';
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          isLoading: true,
          imageUrl: data.message,
        });
      });
      console.log(this.state.imageUrl)
    }

